INSERT INTO `aft_options` VALUES (298,'administrator_email','dp5745@wayne.edu'),(299,'copyright','Some Rights Reserved'),(300,'site_title','American Federation of Teachers Historical Collection'),(301,'author','Wayne State University Library System & Walter P. Reuther Library'),(302,'description','A joint project between the Walter P. Reuther Library and the Wayne State University Library to host primary resources from the AFT historical collections documenting various education reform initiatives that union and school boards have collaborated on, from pre-1983 to present. This will be a continually growing project with the intentions to assist in the debate of US education reform.'),(303,'thumbnail_constraint','200'),(304,'square_thumbnail_constraint','200'),(305,'fullsize_constraint','800'),(306,'per_page_admin','50'),(307,'per_page_public','10'),(308,'show_empty_elements','0'),(309,'path_to_convert','/usr/local/bin'),(13,'migration','42'),(14,'admin_theme','default'),(85,'public_theme','default'),(16,'file_extension_whitelist','aac,aif,aiff,asf,asx,avi,bmp,c,cc,class,css,divx,doc,docx,exe,gif,gz,gzip,h,ico,j2k,jp2,jpe,jpeg,jpg,m4a,mdb,mid,midi,mov,mp2,mp3,mp4,mpa,mpe,mpeg,mpg,mpp,odb,odc,odf,odg,odp,ods,odt,ogg, pdf,png,pot,pps,ppt,pptx,qt,ra,ram,rtf,rtx,swf,tar,tif,tiff,txt, wav,wax,wma,wmv,wmx,wri,xla,xls,xlsx,xlt,xlw,zip'),(17,'file_mime_type_whitelist','application/msword,application/ogg,application/pdf,application/rtf,application/vnd.ms-access,application/vnd.ms-excel,application/vnd.ms-powerpoint,application/vnd.ms-project,application/vnd.ms-write,application/vnd.oasis.opendocument.chart,application/vnd.oasis.opendocument.database,application/vnd.oasis.opendocument.formula,application/vnd.oasis.opendocument.graphics,application/vnd.oasis.opendocument.presentation,application/vnd.oasis.opendocument.spreadsheet,application/vnd.oasis.opendocument.text,application/x-ms-wmp,application/x-ogg,application/x-gzip,application/x-msdownload,application/x-shockwave-fla